targets.expr:
sum(
  argocd_cluster_info{
    namespace=~'$namespace',
    job=~'$job'
  }
)

count(
  count(
    argocd_app_info{
      namespace=~'$namespace',
      job=~'$job'
    }
  )
  by (repo)
)

sum(
  argocd_app_info{
    namespace=~'$namespace',
job=~'$job',
dest_server=~'$cluster',
project=~'$project',

  }
)

sum(
  argocd_app_info{
    namespace=~'$namespace',
job=~'$job',
dest_server=~'$cluster',
project=~'$project',

  }
) by (job, dest_server, project, name, health_status, sync_status)

sum(
  argocd_app_info{
    namespace=~'$namespace',
job=~'$job',
dest_server=~'$cluster',
project=~'$project',

  }
) by (health_status)

sum(
  argocd_app_info{
    namespace=~'$namespace',
job=~'$job',
dest_server=~'$cluster',
project=~'$project',

  }
) by (sync_status)

sum(
  round(
    increase(
      argocd_app_sync_total{
        namespace=~'$namespace',
job=~'$job',
dest_server=~'$cluster',
project=~'$project',

      }[$__rate_interval]
    )
  )
) by (job, dest_server, project, name)

sum(
  round(
    increase(
      argocd_app_sync_total{
        namespace=~'$namespace',
job=~'$job',
dest_server=~'$cluster',
project=~'$project',

        phase=~"Error|Failed"
      }[$__rate_interval]
    )
  )
) by (job, dest_server, project, application, phase)

sum(
  round(
    increase(
      argocd_app_reconcile_count{
        namespace=~'$namespace',
        job=~'$job',
        dest_server=~'$cluster'
      }[$__rate_interval]
    )
  )
) by (namespace, job, dest_server)

sum(
  increase(
    argocd_app_reconcile_bucket{
      namespace=~'$namespace',
      job=~'$job',
      dest_server=~'$cluster'
    }[$__rate_interval]
  )
) by (le)

sum(
  round(
    increase(
      argocd_app_k8s_request_total{
        namespace=~'$namespace',
        job=~'$job',
        project=~'$project'
      }[$__rate_interval]
    )
  )
) by (job, server, project, verb, resource_kind)

sum(
  argocd_kubectl_exec_pending{
    namespace=~'$namespace',
    job=~'$job'
  }
) by (job, command)

sum(
  argocd_cluster_api_resource_objects{
    namespace=~'$namespace',
    job=~'$job',
    server=~'$cluster'
  }
) by (namespace, job, server)

sum(
  argocd_cluster_api_resources{
    namespace=~'$namespace',
    job=~'$job',
    server=~'$cluster'
  }
) by (namespace, job, server)

sum(
  increase(
    argocd_cluster_events_total{
      namespace=~'$namespace',
      job=~'$job',
      server=~'$cluster'
    }[$__rate_interval]
  )
) by (namespace, job, server)

sum(
  increase(
    argocd_git_request_total{
      namespace=~'$namespace',
      job=~'$job',
      request_type="ls-remote"
    }[$__rate_interval]
  )
) by (namespace, job, repo)

sum(
  increase(
    argocd_git_request_total{
      namespace=~'$namespace',
      job=~'$job',
      request_type="fetch"
    }[$__rate_interval]
  )
) by (namespace, job, repo)

sum(
  increase(
    argocd_git_request_duration_seconds_bucket{
      namespace=~'$namespace',
      job=~'$job',
      request_type="fetch"
    }[$__rate_interval]
  )
) by (le)

sum(
  increase(
    argocd_git_request_duration_seconds_bucket{
      namespace=~'$namespace',
      job=~'$job',
      request_type="ls-remote"
    }[$__rate_interval]
  )
) by (le)
